jQuery.validator.addMethod("lettersonly", function(value, element) {
  return this.optional(element) || /^[a-z][\w ]+$/i.test(value);
}, "Enter only Letters");
jQuery.validator.addMethod("alphanumeric", function(value, element) {
	  return this.optional(element) || /^[a-zA-Z]{1,}[0-9]{1,}$/i.test(value);
	}, "it should be alphanumeric eg:12ab34"); 
jQuery.validator.addMethod("emailcheck", function(value, element) {
	  return this.optional(element) || /^.+@.+\..{2,4}$/i.test(value);
	}, "enter a valid email");
jQuery.validator.addMethod("passwordonly", function(value, element) {
	  return this.optional(element) || /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[0-9a-zA-Z#@!$%^&*]{8,}$/i.test(value);
	}, "alphanumeric with special characters minimum 8 char");
jQuery.validator.addMethod("phnocheck", function(value, element) {
	  return this.optional(element) || /^[987]{1}[0-9]\d+$/i.test(value);
	}, "only numbers of 10 digits and it should be valid");
jQuery.validator.addMethod("numbersonly", function(value, element) {
	  return this.optional(element) || /^[0-9]+$/i.test(value);
	}, "Enter in Numbers");
jQuery.validator.addMethod("nozero", function(value, element) {
	  return this.optional(element) || /^[1-9]{1}[0-9]+$/i.test(value);
	}, "Enter valid digits");